    private void btnRegistrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnRegistrarActionPerformed
        // TODO add your handling code here:
        if (!this.txtNombre.getText().isEmpty() && !this.txtApellido.getText().isEmpty() && !this.txtEdad.getText().isEmpty() && !this.txtUsuario.getText().isEmpty() && !this.txtContra.getText().isEmpty()) {
            Usuario usuario = new Usuario();
            usuario.setNombre(this.txtNombre.getText());
            usuario.setApellido(this.txtApellido.getText());
            usuario.setEdad(Integer.parseInt(this.txtEdad.getText()));
            usuario.setUsuario(this.txtUsuario.getText());
            usuario.setContra(this.txtContra.getText());
            Usuarios.add(usuario);
            JOptionPane.showMessageDialog(null, "Registro Exitoso");
        } else{
            JOptionPane.showMessageDialog(null,"Ningun campo puede quedar vacio.");
        }
        this.txtNombre.setText(null);
        this.txtApellido.setText(null);
        this.txtEdad.setText(null);
        this.txtUsuario.setText(null);
        this.txtContra.setText(null);
    }//GEN-LAST:event_btnRegistrarActionPerformed

    private void btnRegresarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnRegresarActionPerformed
        // TODO add your handling code here:
        Login login = new Login(Usuarios);
        login.show(true);
        this.show(false);
    }//GEN-LAST:event_btnRegresarActionPerformed


